"use strict";

var _createMap = require("@ui-schema/ui-schema/Utils/createMap");

var _immutable = require("immutable");

describe('createMap', function () {
  test('createMap plain', function () {
    var map = (0, _createMap.createMap)({});
    expect(_immutable.Map.isMap(map) && !_immutable.OrderedMap.isOrderedMap(map)).toEqual(true);
  });
  test('createMap nested', function () {
    var map = (0, _createMap.createMap)({
      list: [],
      map: {}
    });
    expect(_immutable.Map.isMap(map.get('map')) && !_immutable.OrderedMap.isOrderedMap(map.get('map'))).toEqual(true);
    expect(_immutable.List.isList(map.get('list'))).toEqual(true);
  });
});
describe('createOrderedMap', function () {
  test('createOrderedMap plain', function () {
    var map = (0, _createMap.createOrderedMap)({});
    expect(_immutable.Map.isMap(map) && _immutable.OrderedMap.isOrderedMap(map)).toEqual(true);
  });
  test('createOrderedMap nested', function () {
    var map = (0, _createMap.createOrderedMap)({
      list: [],
      map: {}
    });
    expect(_immutable.Map.isMap(map.get('map')) && _immutable.OrderedMap.isOrderedMap(map.get('map'))).toEqual(true);
    expect(_immutable.List.isList(map.get('list'))).toEqual(true);
  });
});