import { mapSchema } from "@ui-schema/ui-schema/Utils/schemaToNative/schemaToNative";
import { createMap } from "@ui-schema/ui-schema/Utils";
describe('mapSchema', () => {
  test.each([[{}, createMap({
    minLength: 10
  }), res => res.minLength === 10, true], [{}, createMap({
    maxLength: 5
  }), res => res.maxLength === 5, true], [{}, createMap({
    minimum: 1
  }), res => res.min === 1, true], [{}, createMap({
    maximum: 2
  }), res => res.max === 2, true], [{}, createMap({
    multipleOf: 4
  }), res => res.step === 4, true], [{}, createMap({
    pattern: '*'
  }), res => res.pattern === '*', true], [{}, createMap({}), res => Object.keys(res).length === 0, true]])('mapSchema(%j, %j)', (inputProps, schema, compare, expected) => {
    expect(compare(mapSchema(inputProps, schema))).toBe(expected);
  });
});